def part_one(data):
	floor = 0
	for step in data:
		if(step == "("): floor += 1
		else: floor -= 1
	return floor

def part_two(data):
	floor = 0
	for count, step in enumerate(data):
		if(step == "("): floor += 1
		else: 
			floor -= 1
			if(floor < 0): return count+1
	return None

